html,
body {
	font-size: 16px !important;
	overflow-x: hidden;
}

.text-center {
	text-align: center;
}
.text-right {
	text-align: right;
}

.section {
	padding-top: 2rem;
	padding-bottom: 2rem;
	&.home-product-section {
		.card {
			margin-bottom: 0;
		}
	}

	.section-title {
		font-weight: bold;
		line-height: 1.1;
		margin-top: 0rem;
		margin-bottom: 0.5rem;
	}
	.section-intro {
		color: #666b71;
		font-size: 0.875rem;
		line-height: 1.4;
		margin-bottom: 2rem;
	}

	@include breakpoint('medium') {
		&.lg {
			padding-top: 4rem;
			padding-bottom: 4rem;
		}
		// &.home-category-icon-section {
		// 	.section-title {
		// 		margin: 0 0 3rem;
		// 	}
		// }
		.section-title {
			font-size: 1.75rem;
			margin-top: 1rem;
		}
	}
}
.home-category-icon-section {
	.section-title {
		margin-bottom: 1rem;
	}

	.grid-item {
		margin-bottom: 1rem;
	}
	.category-link {
		max-width: 200px;
		margin: auto;
		text-decoration: none;
		img {
			border-radius: 50%;
			display: block;
			margin: 0 auto 1rem;
		}
		h4 {
			margin: 0;
			font-weight: bold;
			font-size: 1rem;
			line-height: 1.3;
			text-align: center;
		}
	}

	@include breakpoint('medium') {
		.section-title {
			margin-bottom: 2rem;
		}
	}
}

.category-icons {
	font-size: 0;
}

.category-icon {
	display: inline-block;
	vertical-align: top;
	width: 50%;
	text-decoration: none;
	margin: 0 auto 1rem;
	.icon {
		display: block;
		margin: auto;
		height: 120px;
		width: 120px;
		background: #f2f2f2;
		border-radius: 50%;
		padding: 35px 0 0;
	}
	@media only screen and (min-width: 376px) {
		.icon {
			width: 160px;
			height: 160px;
			padding: 55px 0 0;
		}
	}
	.icon svg {
		height: 50px;
		width: 50px;
		fill: inherit;
	}
	.title {
		color: $dark-gray;
		font-size: 18px;
		font-weight: bold;
		display: inline-block;
		margin-top: 1rem;
	}
	@include breakpoint('small') {
		width: 33.333%;
		margin: 0 auto 2rem;
	}
	@include breakpoint('medium') {
		width: 25%;
	}
	@include breakpoint('large') {
		width: 16.6667%;
	}
}

.popular-brands {
	@include grid-row($behavior: nest);
	font-size: 0; // 1
	.brand-item {
		@include grid-column(6, $float: none);
		padding-left: 0;
		padding-right: 0;
		display: inline-block;
		vertical-align: top;
		text-decoration: none;
		margin: 0 auto 1rem;
	}
	.img-container {
		background-color: #f2f2f2;
		border-radius: 50%;
		position: relative;
		width: 7.5rem;
		height: 7.5rem;
		margin: 0 auto 1rem;
	}
	.brand-img {
		display: block;
		position: absolute;
		top: 50%;
		left: 50%;
		max-width: 75%;
		transform: translate(-50%, -50%);
	}
	.title {
		color: $dark-gray;
		font-size: 1rem;
		font-weight: bold;
	}
	@media only screen and (min-width: 576px) {
		.brand-item {
			width: 33.333%;
			margin: 0 auto 2rem;
		}
		.img-container {
			width: 10rem;
			height: 10rem;
		}
	}
	@include breakpoint('medium') {
		.title {
			font-size: 1.125rem;
		}
	}
	@media only screen and (min-width: 980px) {
		.brand-item {
			width: 16.667%;
			margin: 0 auto 3rem;
		}
	}
	@include breakpoint('large') {
		.brand-item {
			margin: 0 auto 4rem;
		}
	}
}

.trust-section {
	.row {
		@include grid-row();
		font-size: 0;
	}
	.trust-col {
		@include grid-column(12, $float: none);
		padding-left: 1.25rem;
		padding-right: 1.25rem;
		display: inline-block;
		font-size: fontSize('base'); // 1
		vertical-align: top;
		border-bottom: 2px solid #d7d7d7;
	}
	.trust-col.col-full {
		border: none;
		padding-top: 3rem;
	}
	a {
		display: inline-block;
		vertical-align: top;
		margin: 0 0.25rem 0.5rem 0;
	}
	.security img {
		display: block;
		margin: 0 auto 2rem;
	}

	.col-title {
		color: #424548;
	}

	@include breakpoint('small') {
		.trust-col {
			border: none;
			width: grid-calc(6, $total-columns);
			min-height: 160px;

			&:first-child,
			&:nth-child(3) {
				border-right: 2px solid #d7d7d7;
			}
		}
		.trust-col.col-full {
			min-height: 0;
			width: grid-calc(12, $total-columns);
		}
		.security img {
			display: inline-block;
			margin: 0 1rem 1.5rem;
		}
	}
	@include breakpoint('medium') {
		.trust-col {
			border-right: 2px solid #d7d7d7;
			width: grid-calc(3, $total-columns);
		}
		.trust-col:nth-child(4),
		.trust-col.col-full {
			border: none;
		}
		.trust-col.col-full {
			width: grid-calc(12, $total-columns);
		}
	}
}

.category-header {
	background-color: #006bd6;
	border-radius: 1rem;
	margin: 0 0 1rem;
	overflow: hidden;
	padding: 1rem;
	position: relative;

	.page-heading {
		color: #fff;
		margin: 0;
		line-height: 1;
		position: relative;
	}

	@include breakpoint('small') {
		padding: 2rem;
	}
	@include breakpoint('medium') {
		padding: 2.5rem;
		.page-heading {
			font-size: 2.5rem;
		}
	}
}
.category-header-image {
	position: absolute;
	top: 50%;
	left: 50%;
	transform: translate(-50%, -50%);
	max-width: none;
	min-width: 100%;
	min-height: 100%;
	width: auto;
	z-index: 0;
}

.section.clothing-panels {
	.grid-item {
		margin-bottom: 1.5rem;
	}
	.panel {
		// border: 1px solid #45484a;
		border-radius: 16px;
		overflow: hidden;
	}
	.panel-gradient {
		background: linear-gradient(
			180deg,
			rgba(0, 0, 0, 0) 48.44%,
			rgba(0, 0, 0, 0.5) 100%
		);
		position: absolute;
		top: 0;
		left: 0;
		z-index: 1;
		width: 100%;
		height: 100%;
	}
	.panel-content {
		padding: 1rem;
		text-align: center;
		width: 100%;
		position: absolute;
		bottom: 0;
		left: 0;
		z-index: 2;
		color: #fff;
		h3 {
			color: #fff;
			font-size: 1.75rem;
			margin: 0;
		}
		p {
			display: block;
			font-weight: bold;
			font-size: 0.875rem;
			margin-bottom: 1.5rem;
		}
		.button {
			margin: 0;
		}
	}
	@include breakpoint('small') {
		.panel-content {
			h3 {
				font-size: 1.25rem;
			}
		}
	}
	@include breakpoint('medium') {
		.panel-content {
			padding: 2rem 1rem;
			h3 {
				font-size: 1.75rem;
			}
		}
	}
}

.clothing-categories {
	.grid-item {
		margin-bottom: 2rem;
	}
	.discover-category {
		max-width: 200px;
		margin: auto;
		img {
			border-radius: 50%;
			display: block;
			margin: 0 auto 1rem;
		}
		h4 {
			margin: 0;
			font-weight: bold;
			font-size: 1rem;
			line-height: 1.3;
			text-align: center;
		}
	}

	@include breakpoint('medium') {
		.grid-item {
			margin-bottom: 3rem;
		}
		.discover-category {
			h4 {
				font-size: 1.125rem;
			}
		}
	}
}

.clothing-popular-brands {
	.flex-grid {
		-webkit-box-align: start;
		-ms-flex-align: start;
		align-items: flex-start;
		-webkit-box-pack: center;
		-ms-flex-pack: center;
		justify-content: center;
	}
	.grid-item {
		margin-bottom: 2rem;
